--
-- Структура таблицы `yachts_sale`
--

CREATE TABLE `yachts_sale` (
  `id` int(11) NOT NULL,
  `name` varchar(64) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `length` int(11) DEFAULT NULL,
  `port` varchar(64) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `price` int(11) DEFAULT NULL,
  `capacity` int(11) DEFAULT NULL,
  `power` int(11) DEFAULT NULL,
  `description` text COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `image_path` text COLLATE utf8mb4_unicode_ci DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Дамп данных таблицы `yachts_sale`
--

INSERT INTO `yachts_sale` (`id`, `name`, `length`, `port`, `price`, `capacity`, `power`, `description`, `image_path`) VALUES
(5, 'Яхта Austin Parker 44 Ibiza', 25, 'ХОРВАТИЯ', 720000, 12, 1100, '44\' IBIZA - яхта, созданная в соответствии с требованиями рынка для данного сегмента. Она идеально подойдет тем, кто ценит комфорт открытого кокпита, так необходимого для дневных прогулок, и удобство закрытого салона для круиза средней дальности. Яхта комплектуется тремя видами двигателей: стационарными Cummins с валовым приводом, Volvo Penta с приводом IPS или подвесными – Mercury Verado. Максимальная скорость яхты может достигать 37 узлов и она может проходить до 400 морских миль в экономичном режиме.', 'n_austin_parker_44_ibiza_wa.jpg'),
(6, 'Яхта Frauscher 1414 Demon', 30, 'ИБИЦА', 960000, 12, 2000, 'Плоский нос, Z-образная боковая линия и безрамочное ветровое стекло – почерк дизайнеров австрийской семейной верфи виден с первого взгляда. Лодка одновременно напоминает выныривающего из воды кита и... истребитель Stealth. Продольные реданы на корпусе позволяют быстро выходить на глиссирование. Яхта развивает скорость свыше 40 узлов, сохраняя исключительно плавный ход. На просторной палубе есть уютный кокпит с баром и просторная платформа для купания. Формально Demon вмещает 12 человек, но фактически в кокпите можно разместить и гораздо больше гостей. Центральное расположение консоли управления позволяет свободно перемещаться по лодке. Пол кокпита выстелен натуральным тиком. Для отделки сидений и лежаков использован сверхпрочный винил, не нагревающийся на солнце. Внутри яхты поместилась полностью оборудованная ванная комната и две просторные каюты с кроватями для четырёх человек. Высота потолка в каюте – 196 см. Можно встать в полный рост.', '1414-demon_port-adriano_17_.jpg'),
(7, 'Яхта Ferretti Yachts 500 #7', 40, 'САНКТ-ПЕТЕРБУРГ', 1217300, 12, 1533, 'Яхта сразу привлекает внимание агрессивными линиями корпуса и надстройкой с эффектом левитирующей (парящей) крыши. У Ferretti Yachts 500 самый просторный флайбридж в классе, носовая часть с трансформирующимися шезлонгами и купальная платформа с надежным приводом. Барная зона салона объединяется с просторным кокпитом благодаря открываемой секции окна салонной двери. На кухне обустроены специальные места для хранения посуды, а в центральной части салона расположена полноценная обеденная зона. Уютная мастер-каюта занимает пространство от борта до борта центральной части нижней палубы, к услугам владельца яхты - отдельная ванная комната, два вместительных гардероба для личных вещей, кровать Queen size и домашний кинотеатр. Гостей можно разместить в VIP-каюте в носовой части нижней палубы и в  гостевую каюте по правому борту. Также яхта оснащена отдельной каютой капитана в кормовой части. Высота потолков во всех помещениях яхты превышает 2 м. Даже в базовом оснащении предлагается несколько вариантов отделки интерьера из высококачественных материалов.', 'ferrettiyachts500newexteriors_0017_47671.jpg'),
(8, 'Яхта Riva Dolceriva #7', 33, 'БАЛАКЛАВА', 1848750, 10, 1492, 'Самая большая модель линии Open заменила легендарную Rivarama. Новинка на 4 фута длиннее и выглядит, как итальянский суперкар с карбоновыми воздухозаборниками. Пост управления яхтой оснащен системой мониторинга и навигации Garmin Empirbus. Такой плавности хода Вы не найдете ни на одной другой лодке. За счет морских обводов «глубокое V» Dolceriva рассекает морскую волну, как нож масло. Электрогидроусилитель штурвала настроен таким образом, что Вы с легкостью сможете управлять яхтой всего одним пальцем. Опционально можно поставить джойстик. Перед лобовым стеклом спрятан выдвижной тент-бимини. Он ставится и убирается одним нажатием кнопки на приборной панели. Платформа для купания cо встроенными шезлонгами и отсеками для SEABOB откидывается при помощи сервопривода. Внутри яхты расположены салон с диванами белой кожи вдоль стен и мастер-каюта с большой двуспальной кроватью и двумя гардеробными, отгороженная от столовой раздвижной дверью из стали и черного стекла. Оба помещения роскошно отделаны стеновыми панелями из лакированного красного дерева и сталью.', 'rivadolcerivacruising_0009_41526.jpg'),
(9, 'Яхта Riva 56\' Rivale #36', 50, 'МОНТЕ-КАРЛО', 2287000, 10, 1727, 'Динамика, элегантность и спортивный дух делают открытые яхты Riva столь узнаваемыми. Роскошный внешний вид Rivale не противоречит функциональности дизайна. Эргономика продумана до мелочей – от панорамного остекления по периметру нижней палубы и просторной обеденной зоны до вместительного гаража для тендера, спрятанного под двойной дверью-ракушкой из углепластика. Под аркой- каркасом, добавляющей акцент в дизайн яхты и выступающей в качестве несущей опоры для антенн и электронного оборудования, расположился небольшой лаунж, обращенный в сторону носа. Платформа для купания опускается под воду, чтобы облегчить спуск и подъём тендера. В подводном положении платформы по левому борту открываются ступени, по которым гости после купания поднимаются на яхту. Стандартная планировка предусматривает гостевую каюту и мастер-каюту на миделе, то есть в самой широкой части яхты. В качестве опции можно установить гиростабилизатор качки.', '3bcdfee39cc75b502b92f0405216cd01.jpg'),
(10, 'Яхта Ferretti Yachts 670 #33', 32, 'МОНТЕ-НЕГРО', 2646925, 18, 2024, 'Абсолютно новая 20-метровая модель, выпущенная к 50-летию Ferretti Yachts, прекрасно сочетает маневренность, скорость и комфорт. Яхта выделяется просторной и комфортабельной носовой зоной отдыха cо столиками, трансформируемыми в широкую зону для загара, одним из самых больших флайбриджей со свободным расположением мебели, корпусом класса «А» и широкими возможностями кастомизации внутренних пространств. Сидя на диванах в салоне, можно видеть в окно линию горизонта и море так же хорошо, как и с палубы. В мастер-каюте с кроватью Queen size, ванной комнатой и гардеробным помещением отведено пространство для полноценного кабинета c письменным столом, креслом и полками для документов. По желанию клиента кабинет можно трансформировать в четвертую гостевую каюту. Помимо каюты владельца, на нижней палубе располагаются VIP и гостевая каюты, а помещение для экипажа создает дополнительную шумоизоляцию между машинным отделением и жилыми зонами. Кормовая часть Ferretti Yachts 670 оснащена широкой купальной платформой на гидравлическом червячном приводе, гаражом для хранения двух SeaBob и полностью складываемым трапом. ', '86053e7a946a1c8d42a2b6ea6bea14ad.jpg'),
(11, 'Яхта Riva Riva 68\' Diable Project #1', 37, 'ЧЕРНОГОРИЯ', 3445200, 16, 2067, 'Новая Riva 68 Diable идеально вписывается между Riva 56 Rivale и Riva 76 Bahamas немного опережая их в максимальной скорости. 68 Diable — прекрасный вариант для комфортного уикенда с комфортным размещением для 6 гостей. Хардтоп со сдвижным люком и бимини с электроприводом делает Diable универсальной для разнообразных условий. 68 Diable, конечно же, совершенно великолепна, но при этом прекрасно продумана, чтобы доставить несравненные впечатления от круиза. Помимо шезлонгов на носу и в кокпите, у кромки воды на купальной платформы для купания есть еще один раскладной шезлонг.<br/>\r\n<br/>\r\n	 Дизайн интерьера Riva 68 Diable, как никогда, потрясающий, он сочетает в себе фирменное качество и детализацию Riva с беспрецедентным комфортом во время круиза. Изысканно обставленная планировка с тремя каютами включает в себя апартаменты владельца во всю ширину, а также двухместные апартаменты и апартаменты с двумя односпальными кроватями. В гостиной с открытой планировкой предусмотрена обеденная зона на 6 мест и кухня. Огромные окна корпуса обеспечивают прекрасное естественное освещение всего помещения с потрясающим видом на море.', 'whatsapp-image-2021_07_28-at-15.52.38.jpeg'),
(12, 'Яхта Ferretti Yachts 780 #38', 22, 'ФЕОДОСИЯ', 4449350, 20, 2401, 'Фантастическая флайбриджная Ferretti Yachts 780 - это не просто яхта с планировкой помещений, схожей с мега-проектами от 30 метров, но и судно, сертифицированное для эксплуатации в открытом океане.На флайбридже возможны различные вариации со свободно стоящей мебелью и модификациями хардтопа, а в носовой части продуманы вместительная обеденная зона и просторная ложа для загара. В кормовой части предусмотрены два изящных хранилища под буксировщики SeaBob. Купальная платформа полностью погружается под воду и с легкостью выдерживает вес 500-килограммового тендера или гидроцикла. Салон с кинотеатром и обеденной зоной рассчитан на 9 гостей и полностью отделен от помещений для экипажа звуконепроницаемыми дверьми. Мастер–каюта на нижней палубе удивляет своей продуманной компоновкой с отдельной гардеробной комнатой, просторной ванной комнатой, которые отделяют владельца от машинного отделения дополнительными двумя метрами шумоизоляции. VIP и гостевые каюты оснащены собственными ванными комнатами и гардеробными шкафами для одежды и чемоданов. Дополнительная дверь на кухне с проходом на боковую палубу позволяет экипажу работать без встреч с владельцем и гостями.', '0c88ffb750b93a627273944fbc7f55a6.jpg'),
(13, 'Яхта Riva 76\' Perseo Super Project #22', 45, 'САРДИНИЯ', 4891906, 16, 2325, 'Жизнь – это скорость, свобода и море удовольствия, которые обещает обладание рестайлинговой моделью яхты Riva 76’ Perseo Super. Яхта приковывает взгляд своей спортивной элегантностью и дизайнерскими решениями. Особенно впечатляет площадь остекления судна, которая достигает 40 кв. м. Капитан имеет максимально возможный обзор благодаря широкому лобовому стеклу в салоне. «Бесшовная» поверхность лобового стекла простирается до флайбриджа. Планировка флайбриджа изменена в сторону большего комфорта для отдыха на лежаках, а в кокпите обеденный стол теперь можно трансформировать в ложу для загара и добавить барный модуль между салоном и кокпитом. Заниженный фальшборт вдоль боковых окон надстройки позволит Вам изнутри наслаждаться пейзажем, а раздвижная стеклянная перегородка между кокпитом и салоном открывает обзор за кормой. Кормовая часть была задумана так, чтобы обеспечить комфорт для гостей и удобство пользования для экипажа. Полностью погружная купальная платформа обеспечивает удобство спуска и подъёма тендера и является зоной отдыха для владельца и его гостей. Необычным, но элегантным решением в дизайне экстерьера стало то, что швартовочное оборудование скрыто за решётками из нержавеющей стали.', 'riva76_perseosuperprojectcruising_0002_48614.jpg'),
(14, 'Яхта Mangusta OCEANO 43', 32, 'РИМИНИ', 430000, 12, 1320, 'Первая трехпалубная водоизмещающая яхта верфи и первая – из стали и алюминия. Ее линиями хочется любоваться, постоянно открывая для себя что-то новое, будто рассматриваешь скульптуру эпохи Возрождения. Стеклянные стены, прозрачные секции фальшборта, просторные открытые палубы со встроенными в пол иллюминаторами призваны создавать ощущение «прозрачной» яхты, размывая границы между внутренним пространством и внешней средой. Максимальный эффект достигается на стоянке, когда открываются кормовой пляжный клуб, балкон в каюте владельца и большие раздвижные двери по бокам главного салона. Даже на водоизмещающей яхте трехпалубной компоновки верфь сохранила верность некоторым решениям, вытекающим из конструктивных особенностей MaxiOpen, таким как наличие полноценной зоны отдыха с бассейном в носовой части. Яхта рассчитана на 12 гостей, развивает максимальную скорость в 15 узлов и способна пройти 4500 морских миль без дозаправки на крейсерской скорости в 11 узлов.', 'mo43_navigation-_3_.jpg'),
(15, 'Яхта Mangusta GranSport 33', 44, 'НЕВШАТЕЛЬ', 330000, 12, 1200, 'Линейка Mangusta GranSport представляет собой технический и стилистический синтез двух других модельных рядов: MAXI OPEN и OCEANO. Это быстроходные водоизмещающие яхты с большой дальностью хода. Сочетание гидродинамических параметров корпуса и мощность двигателей обеспечивают наилучшую производительность и способность достигать более высоких скоростей, типичных для быстроходных яхт, когда это необходимо.', 'mangusta_gs_33_7.jpg'),
(16, 'Яхта Mangusta 104 REV', 33, 'НЕВШАТЕЛЬ', 3180000, 10, 3180, 'Ультрасовременное прочтение стиля быстрых и комфортных яхт Mangusta Maxi Open от российского дизайнера мегаяхт Игоря Лобанова.\r\nЯхта сразу узнаваема по волнообразному непрерывному остеклению нижней палубы, которое даёт много естественного света в мастер-каюте и четырёх гостевых каютах. Огромный размер окон обеспечивает великолепную видимость. При этих особенностях экстерьера. Mangusta Sport 104 соответствует классу BV и коду MCA для коммерческой регистрации. Инновационный дизайн гаража подчеркивает агрессивный профиль яхты и позволяет разместить в нём тендер длиной 5 м, а также гидроцикл. Двигатели MTU 16V 2000 M96L и водомётные силовые установки Kamewa позволят развивать скорость до 35 узлов. ', 'book-msport-104.jpg');

